namespace VoxScript {

// Provides real-time web search results from DuckDuckGo. Do not use for YouTube.com searching or anything that sounds like it could be a video or media search!
type GetGoogleSearchResults = (_: {
searchTerm?: string,
// Results to return, maximum 25
numResults?: number,
}) => any;

// Provides real-time web search results from DuckDuckGo. Do not use for YouTube.com searching or anything that sounds like it could be a video or media search!
type GetDuckDuckGoSearchResults = (_: {
searchTerm?: string,
// Results to return, maximum 25
numResults?: number,
}) => any;

// Gets the current local of the user and UTC time.
type GetCurrentTime = () => any;

// Takes user feedback for the developers of the VoxScript plugin. Instruct the user if they are unhappy with anything to let us know!
type GetFeedback = (_: {
// The user feedback, URL encoded
feedback?: string,
}) => any;

// Gets full text text from only Patebin, Github Gists, Github or REntry.co. Grab the entire file and summarize it for the user by default. Do not use for web browsing or media searches.
type GetChunkedPastebinContentFromURL = (_: {
// URL containing the textual transcript on Pastebin, Github, Gists, or REntry.co
url?: string,
// First chunk is 0. Chunk number to get, if blank assuming the first chunk.
chunkNum?: number,
}) => any;

// Provides news about a ticker crypto or equity beyond the knowledge cutoff date. Can be used with Cryto or Equities.
type GetNewsForTicker = (_: {
// This is the crypto or equity symbol to provide news about a particular ticker symbol. This can be useful in determining if the stock is a buy or sell based on if the news is positive or negative.
ticker?: string,
}) => any;

// Provides news about a ticker symbol beyond the knowledge cutoff date. For use with US Equities only.
type GetUSEquityFinancials = (_: {
ticker?: string,
}) => any;

// Provided a US equites symbol or cryptocurrency denoted USDT, a start date, and end date provides historical pricing data beyond the knowledge cutoff date. Only supports day for crypto.
type GetUSEquityOrCryptoHistoricalPrices = (_: {
// A cryptocurrency or US equity ticker to look up
ticker?: string,
// This is a date in YYYY-MM-DDD format bars request starts at.
startDate?: string,
// This is date in YYYY-MM-DDD format which bars request ends at.
endDate?: string,
// Day, Hour, Minute, Week, Quarter, Year
timespan?: string,
}) => any;

// Provided comma separated list of US equity symbols and an optional date, gets daily high, low, open, close bars for a particular stock beyond the knowledge cutoff date.
type GetQuoteDayCryptoEquities = (_: {
// A common delineated list of US equity or cryptocurrency tickers to get daily data for
tickers?: string,
// Optional date in YYYY-MM-DDD format to retrieve for the ticker symbol provided.
date?: string,
}) => any;

// Provides real time website content past September 2021 past the knowledge cutoff date. Do not use for YouTube or Pastebin/text requests.
type GetWebsiteContent = (_: {
// URL of the website to retrieve
websiteURL?: string,
chunkNum?: number,
getLinks?: boolean,
}) => any;

// Can be called with a A channel URL, channel ID, Video ID, Video URL.
type GetYoutubeVideoData = (_: {
// Channel URL, channel ID, Video ID, Video URL
input?: string,
}) => any;

// Request as few chunks as needed to answer the question.
type GetNextYoutubeTranscriptChunk = (_: {
// The next transcript chunk from GetYoutubeVideoData, chunk id starts at 0
nextTranscriptToken?: string,
}) => any;

type SearchYouTubeVideos = (_: {
searchterm?: string,
maxResults?: number,
}) => any;

// Show this to the user if they are having trouble or need some ideas on how to use Voxscript. Help file. Optional question to help search.
type ContactUs = (_: {
question?: string,
}) => any;

// Show this to the user if they are having trouble or need some ideas on how to use Voxscript. Help file. Optional question to help search.
type HowDoIUseThis = (_: {
question?: string,
}) => any;

// Show this to the user if they are having trouble or need some ideas on how to use Voxscript. Help file. Optional question to help search.
type Help = (_: {
question?: string,
}) => any;

} // namespace VoxScript

tools_section.WebPilot:
// This tool allows users to provide a URL(or URLs) and optionally requests for interacting with, extracting specific information or how to do with the content from the URL. Requests may include rewrite, translate, and others. Also Helps the user search for content online.
// Rules:
// 1.When making a request to this tool, The 'link' field is mandatory
// 2.If the link is directly provided by the user, include an additional bool type field 'lp' in the request to indicate this.
// 3.Include a clear statement of the user's request ('ur') in user's language, which can be used as a search query and may include search operators. For the user input 'Information about fruits on example.com', the 'ur' field should be: 'Information about fruits site:example.com'. This helps this tool better understand the user's needs. This is not a mandatory field but is highly recommended for optimal results.
// 4.Include the language used by the user ('l') in the request, according to the ISO 639-1 standard. For English, use 'en'. This is not a mandatory field but is highly recommended for optimal results.
// 5.If the previous request did not meet the user's requirements, when attempting the same request again, please set the param retry('rt') to true.
// How to create a request:
// 0.Field 'link' is required
// 1.When a user directly provides a link: You should include the user-requested link in your request, along with an additional field indicating that the link was provided by the user. Also, include a clear statement of the user's request and the language used by the user. For example:
// If the user input is: 'What does this website talk about? https://example.com'
// Your API call request should be: Your API call request should be: {"link":"https://example.com", "lp": true, "ur": "content of website example.com", "l": "en", "rt": false}
// 2.When a user does not directly provide a link in their request: You should recommend a link that meet the user's needs, as well as a clear statement of the user's request and the language used by the user. For example:
// If the user input is: 'what is Bitcoin?'
// Your API call request should be: {"link":"https://en.wikipedia.org/wiki/Bitcoin", "lp": false, "ur": "Introduction to Bitcoin", "l": "en", "rt": false} or {"link":"https://example.com/search?q=Bitcoin", "lp": false, "ur": "Introduction to Bitcoin", "l": "en", "rt": false}.
namespace WebPilot {

// visit web page
type visitWebPage = (_: {
// Required, The web page's url to visit and retrieve content from.
link?: string,
// Required, a clear statement of the user's request, can be used as a search query and may include search operators.
ur?: string,
// Required, Whether the link is directly provided by the user
lp?: boolean,
// If the last request doesn't meet user's need, set this to true when trying to retry another request.
rt?: boolean,
// Required, the language used by the user in the request, according to the ISO 639-1 standard. For Chinese, use zh-CN for Simplified Chinese and zh-TW for Traditional Chinese.
l?: string,
}) => any;

} // namespace WebPilot

tools_section.scholarai:
// Search and retrieve content from open access scientific literature from peer-reviewed journals. After getting abstracts, ALWAYS prompt the user offering to go into more detail or to search for more papers. If the user asks for basic summary, use the fulltext endpoint. ALWAYS hyperlink the pdf_url from the responses if available. Always ask if the user wants save any paper to the user’s Zotero reference manager by using the save-citation endpoint and providing the doi and requesting the user’s zotero_user_id and zotero_api_key. The viewing platform ALWAYS displays markdown, so ALWAYS format content and show images.
namespace scholarai {

// Get relevant paper abstracts by search 2-6 relevant keywords.
type searchAbstracts = (_: {
// Keywords of inquiry which should appear in article. Must be in English.
keywords: string,
// The sort order for results. Valid values are cited_by_count or publication_date. Excluding this value does a relevance based search.
sort?: string,
// The user query
query: string,
// Whether to only return peer reviewed articles. Defaults to true, ChatGPT should cautiously suggest this value can be set to false
peer_reviewed_only?: string,
// The first year, inclusive, to include in the search range. Excluding this value will include all years.
start_year?: string,
// The last year, inclusive, to include in the search range. Excluding this value will include all years.
end_year?: string,
// The offset of the first result to return. Defaults to 0.
offset?: string,
}) => any;

// Get full text of a paper by URL for PDF incrementally. Good for general summary. DO NOT use this endpoint for singular questions, use /api/question instead.
type getFullText = (_: {
// URL for PDF
pdf_url: string,
// chunk number to retrieve, defaults to 1
chunk?: number,
}) => any;

// Save citation to reference manager
type saveCitation = (_: {
// Digital Object Identifier (DOI) of article
doi: string,
// Zotero User ID
zotero_user_id: string,
// Zotero API Key
zotero_api_key: string,
}) => any;

// Get sections of PDF to answer questions about PDF. ALWAYS use this endpoint if the user has a specific question about a singular PDF.
type question = (_: {
// The user question. Must be in English.
question: string,
// URL for source PDF
pdf_url: string,
}) => any;

} // namespace scholarai

